import { useRef } from 'react';
import './new-conversation-modal.styles.scss';

const NewConversationModal = ({ showModal, closeModal }) => {
  const idRef = useRef();
  const nameRef = useRef();

  return (
    <div
      className={`new-conversation-modal-container ${
        showModal ? 'active' : ''
      }`}
      onClick={closeModal}
    >
      <div
        className="new-conversation-modal-content"
        onClick={e => e.stopPropagation()}
      >
        <div className="new-conversation-modal-header">
          <p>Create Contact</p>
          <button onClick={closeModal} type="button">
            &10006;
          </button>
        </div>
        <div className="new-conversation-modal-body">
          <form>
            <label>Id</label>
            <input ref={idRef} type="text" required />
            <label>Name</label>
            <input ref={nameRef} type="text" required />
            <button type="submit">Create New Chat Room</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default NewConversationModal;
