import { forwardRef, useRef } from 'react';
import { CSSTransition } from 'react-transition-group';
import './chat-participant.styles.scss';

const ChatParticipant = forwardRef(
  ({ chatParticipant, handleRemoveUser }, ref) => {
    const { _id, name } = chatParticipant;

    const participantRef = useRef();

    return (
      <CSSTransition
        in={true}
        timeout={500}
        mountOnEnter
        unmountOnExit
        classNames="item"
      >
        <div ref={ref} className="chat-participant-content-container">
          <div className="chat-participant-content-participant">{name}</div>
          <div
            name={_id}
            onClick={handleRemoveUser}
            className="chat-participant-content-remove"
          >
            &#x2715;
          </div>
        </div>
      </CSSTransition>
    );
  }
);

export default ChatParticipant;
