import { useState } from 'react';
import { useAuthentication } from '../../contexts/authentication-context';
import { useContacts } from '../../contexts/contacts-context';
import { useSidebar } from '../../contexts/sidebar-context';
import Contact from '../contact/contact.component';
import './contact-preview.styles.scss';

const ContactPreview = () => {
  const { friends, currentFriend, setCurrentFriend } = useContacts();

  const handleClick = e => {
    const friendId = setCurrentFriend(e.target.getAttribute('name'));
    const activeFriend = friends.find(friend => friend._id === friendId);
    setCurrentFriend(activeFriend);
  };

  return (
    <div className="contact-preview-container" onClick={handleClick}>
      {friends.length > 0 &&
        friends.map((contact, i) => (
          <Contact
            active={contact.userName === currentFriend ? true : false}
            key={i}
            contact={contact}
          />
        ))}
    </div>
  );
};

export default ContactPreview;
