  const handleKeyPress = e => {
    if (e.code === 'Enter') {
      setNewMessage('');
      return;
    }
  };

  const handleChange = e => {
    console.log(e);
    setNewMessage(e.target.innerText);
  };

  return (
    <div
      // onClick={e => {
