import { useRef, useState } from 'react';
import './send-message.styles.scss';

const SendMessage = ({ newMessage, setNewMessage }) => {
  const handleKeyDown = e => {
    if (e.key === 'Enter') {
      e.preventDefault();
      setNewMessage(e.target.innerHTML);
      e.target.innerHTML = '';
    }
  };

  // const handleChange = e => (e.target.innerHTML = '');

  return (
    <div
      className="send-message-editable"
      data-text={`Message `}
      contentEditable
      onKeyDown={handleKeyDown}
    />
  );
};

export default SendMessage;
