import { useConversations } from '../../contexts/conversations-context';
import './text-header.styles.scss';
import { generateChatNameForSoloChats } from '../../utils/utils';
import { useAuthentication } from '../../contexts/authentication-context';
import { useSidebar } from '../../contexts/sidebar-context';

const TextHeader = () => {
  const { activeChat } = useConversations();
  const { currentUser } = useAuthentication();
  const { setShowModal, setModalType } = useSidebar();

  const handleClick = () => {
    setModalType('user');
    setShowModal(true);
  };

  return (
    <div className="text-header-container">
      <p>
        {activeChat && activeChat.chatName === 'solo chat'
          ? generateChatNameForSoloChats(activeChat[0].users, currentUser)
          : activeChat[0].chatName}
      </p>
      <div className="chat-information-button-container">
        <button onClick={handleClick} className="chat-information-button">
          i
        </button>
      </div>
    </div>
  );
};

export default TextHeader;
