import { forwardRef, useRef, useState } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import './chat-participant.styles.scss';

const ChatParticipant = ({ chatParticipant, handleRemoveUser }) => {
  const { _id, name } = chatParticipant;
  const [show, setShow] = useState(true);
  const node = useRef();

  return (
    // <CSSTransition
    //   in={show}
    //   mountOnEnter
    //   unmountOnExit
    //   timeout={500}
    //   classNames="item"
    //   nodeRef={node}
    // >
    <TransitionGroup>
      <CSSTransition
        in={show}
        mountOnEnter
        unmountOnExit
        timeout={500}
        classNames="item"
      >
        <div
          onClick={() => setShow(false)}
          // ref={node}
          className="chat-participant-content-container"
        >
          <div className="chat-participant-content-participant">{name}</div>
          <div
            name={_id}
            onClick={handleRemoveUser}
            className="chat-participant-content-remove"
          >
            &#x2715;
          </div>
        </div>
      </CSSTransition>
    </TransitionGroup>
  );
};

export default ChatParticipant;
