import { createContext, useContext, useEffect, useState } from 'react';

const SidebarContext = createContext();

export const useSidebar = () => useContext(SidebarContext);

export const SIDEBAR_CATEGORY_TYPE = {
  conversations: 'conversations',
  friends: 'friends',
};

export const SidebarProvider = ({ children }) => {
  const [search, setSearch] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  const [sideBarCategory, setSideBarCategory] = useState(
    SIDEBAR_CATEGORY_TYPE.conversations
  );
  const [showModal, setShowModal] = useState(false);

  const updateSearchValue = e => setSearch(e.target.value);

  const handleSearchSubmit = e => {
    // Spinner until you get the results, fetch them
    e.preventDefault();
    setIsLoading(true);

    setSearch('');
  };

  useEffect(() => {
    // Fetch depending on the value in the search for both posts and users
    // fetch('http://localhost:4000/api/user', {
    //   method: 'post',
    //   headers: { 'Content-Type': 'application/json' },
    //   body: JSON.stringify({
    //     email: text.email,
    //     password: text.password,
    //     name: text.name,
    //     picture: picCloudUrl,
    //   }),
  }, [search]);

  return (
    <SidebarContext.Provider
      value={{
        search,
        updateSearchValue,
        isLoading,
        searchResults,
        sideBarCategory,
        setSideBarCategory,
        setIsLoading,
        setSearchResults,
        showModal,
        setShowModal,
        handleSearchSubmit,
      }}
    >
      {children}
    </SidebarContext.Provider>
  );
};
