import { useState } from 'react';
import { useAuthentication } from '../../contexts/authentication-context';
import { useContacts } from '../../contexts/contacts-context';
import { useSidebar } from '../../contexts/sidebar-context';
import Contact from '../contact/contact.component';
import './contact-preview.styles.scss';

const ContactPreview = () => {
  const { friends, setFriends, currentFriend, setCurrentFriend } =
    useContacts();

  const handleClick = e => {
    setCurrentFriend(e.target.getAttribute('name'));
  };

  return (
    <div className="contact-preview-container" onClick={handleClick}>
      {contacts.length > 0 &&
        contacts.map((contact, i) => (
          <Contact
            active={contact.userName === currentContact ? true : false}
            key={i}
            contact={contact}
          />
        ))}
    </div>
  );
};

export default ContactPreview;
