import { useRef } from 'react';
import { CSSTransition } from 'react-transition-group';
import './chat-participant.styles.scss';

const ChatParticipant = ({
  chatParticipants,
  chatParticipant,
  handleRemoveUser,
}) => {
  const { _id, name } = chatParticipant;
  const nodeRef = useRef();

  return (
    <CSSTransition
      in={chatParticipants.some(participant => participant._id === _id)}
      timeout={300}
      classNames="chat-participant"
      // unmountOnExit
      // mountOnEnter
      nodeRef={nodeRef}
    >
      <div ref={nodeRef} className="chat-participant-content-container">
        <div className="chat-participant-content-participant">{name}</div>
        <div
          name={_id}
          onClick={handleRemoveUser}
          className="chat-participant-content-remove"
        >
          &#x2715;
        </div>
      </div>
    </CSSTransition>
  );
};

export default ChatParticipant;
