import { useMemo } from 'react';
import { useAuthentication } from '../../contexts/authentication-context';
import { useChatView } from '../../contexts/chat-view-context';
import { areFriends } from '../../utils/utils';
import './user-info-dropdown.styles.scss';

const UserInfoDropdown = () => {
  const { currentUser } = useAuthentication();
  const { activeUserInfo } = useChatView();

  const areFriends = useMemo(
    () => areFriends(currentUser, activeUserInfo),
    [currentUser, activeUserInfo]
  );

  return (
    <div className="user-info-dropdown-container">
      <p>{`${areFriends ? 'Remove' : 'Add'} Friend`}</p>
      <p>Send Message</p>
    </div>
  );
};
export default UserInfoDropdown;
