import './search-result.styles.scss';

export const SEARCH_RESULT_TYPE = {
  default: 'default',
  addUserToExistingChat: 'addUserToExistingChat',
};

const SearchResult = ({
  chatParticipants,
  searchResult,
  handleAddUser,
  type,
}) => {
  const { _id } = searchResult;

  return (
